import './App.css';
import Sidebar from './components/Sidebar';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Home from './pages/Home';
import WeatherToday from './pages/Todayweather';
import Profile from './pages/Profile';
import Settings from './pages/Settings';
import React from 'react';
import FiveDayForecast from "./pages/FiveDayForecast"


const API_URL='https://api.openweathermap.org/data/2.5';
const API_KEY = "2030f01fb424bf3b7aee4dac694ffd71";




class App extends React.Component {

  state = {
    weatherData: [],
    location: "zip=10302",
    city: "New York",
    country: "US",
    degreeType: "imperial",
    temp:null,
    lat:51.51549,
    lon:-0.12635,
  }


  componentDidMount = () => {

    var weather_API=`https://api.openweathermap.org/data/2.5/weather?lat=${this.state.lat}&lon=${this.state.lon}&appid=${API_KEY}`;

    fetch(weather_API)
    .then(res => res.json())
    .then(result => {
      this.setState({weatherData: result,
        city:result.name
      })
      console.log( "Result loaded by esther",result);
    });


  }

  render(){

  return (
    <Router>
      <Sidebar />
      <Switch>
      <Route exact path="/Home" render={() => (

         <Home Message="Hi UserName" City={this.state.city}/>
   )}/>
      <Route  path='/Todayweather' exact component={WeatherToday} />
      <Route  path='/FiveDayForecast' exact component={FiveDayForecast} />
      <Route path='/Profile' exact component={Profile} />
      <Route path='/Settings' exact component={Settings} />
      </Switch>
    </Router>
  );
}
}

export default App;
