import './App.css';
import React from 'react';
import Todayweather from "./src/pages/Todayweather"
import stormIcon from "./icons/storm.ico";
import snowIcon from "./icons/snow.ico";
import sunIcon from "./icons/sun.ico";
import rainyIcon from"./icons/rain.ico";
import {useEffect, useState} from "react";
import hazeIcon from "./icons/haze.ico";
import cloudIcon from "./icons/cloud.ico"
import precipitation from "./icons/rain.ico";


const API_KEY = "2030f01fb424bf3b7aee4dac694ffd71";

class Today extends React.Component {

      state = {
        city: "New York",
        country: "US",
        degreeType: "imperial",
        temp:null,
        lat:51.51549,
        lon:-0.12635,
        wind:null,
      description:null,
      humidity:null,
      rangeId:null
      }

       get_WeatherIcon(rangeId) {
            switch (true) {
              case rangeId >= 200 && rangeId < 232:
                return(stormIcon);
              case rangeId >= 300 && rangeId <= 321:
                return(rainyIcon);
              case rangeId >= 500 && rangeId <= 521:
                return(rainyIcon);
              case rangeId >= 600 && rangeId <= 622:
                return(snowIcon);
              case rangeId >= 701 && rangeId <= 781:
                return(hazeIcon);
              case rangeId === 800:
                return(sunIcon);
              case rangeId >= 801 && rangeId <= 804:
                return(precipitation);
              default:
                return(cloudIcon);;
            }
          }


      componentDidMount = () => {

        var weather_API=`https://api.openweathermap.org/data/2.5/weather?lat=${this.state.lat}&lon=${this.state.lon}&appid=${API_KEY}`;

        fetch(weather_API)
        .then(res => res.json())
        .then(result => {
          this.setState({weatherData: result,
            city:result.name,
            temp:result.main.temp,
            wind:result.wind.speed,
            description: result.weather[0].description,
            humidity:result.main.humidity,
            rangeId:result.weather[0].id,
          })
          console.log( "Result loaded by esther",result);
        });


      }

      render(){

      return (
      <Todayweather City={this.state.city} temperature={this.state.temp} Icon={this.get_WeatherIcon(this.rangeId)} Wind={this.state.wind} Humidity={this.state.humidity} Description={this.state.description}></Todayweather>
      );
    }
    }

    export default Today;
