import './App.css';
import Sidebar from './components/Sidebar';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Home from './pages/Home';
import WeatherToday from './pages/Todayweather';
import Profile from './pages/Profile';
import Settings from './pages/Settings';
import React from 'react';
import FiveDayForecast from "./pages/FiveDayForecast"


class App extends React.Component {

  state = {
    weatherData: [],
    location: "zip=10302",
    city: "New York",
    country: "US",
    degreeType: "imperial",
    temp:null,
  }

  componentDidMount = () => {
    const weatherURL = `http://api.openweathermap.org/data/2.5/forecast?${this.state.location},${this.state.country}&units=${this.state.degreeType}&APPID=c49b58637f116ba62e71ca04a520f9bb`;
    fetch(weatherURL)
    .then(res => res.json())
    .then(data => {
      // data returned by API output to console
      console.log("Weather loaded by Esther", data)
      // filter data for time 18:00
      const dailyData = data
      this.setState({weatherData: dailyData

      })
    })
  }

  render(){

  return (
    <Router>
      <Sidebar />
      <Switch>
      <Route exact path="/Home" render={() => (

         <Home Message="Hi UserName" City={this.state.weatherData.name}/>
   )}/>
      <Route  path='/Todayweather' exact component={WeatherToday} />
      <Route  path='/FiveDayForecast' exact component={FiveDayForecast} />
      <Route path='/Profile' exact component={Profile} />
      <Route path='/Settings' exact component={Settings} />
      </Switch>
    </Router>
  );
}
}

export default App;
